function calculateRegexScores(
  query: string,
  keywords: Intents,
  regexWeight: number,
  highKeywords: Intents
): Scores {
  const scores: Scores = {};

  for (const category in keywords) {
    const words = keywords[category];
    const matchedKeywords = words.filter(word => query.includes(word));

    const categoryScore = matchedKeywords.reduce((acc, word) => {
      return acc + (highKeywords[category]?.includes(word) ? highWeightMultiplier : regularWeight);
    }, 0);

    scores[category] = categoryScore * regexWeight;
    console.log(`Matched keywords for ${category}: ${matchedKeywords}`);
  }

  return scores;
}

function normalizeScores(scores: Scores): Scores {
  const total = Object.values(scores).reduce((acc, score) => acc + score, 0);
  if (total > 0) {
    return Object.fromEntries(
      Object.entries(scores).map(([category, score]) => [category, score / total])
    );
  } else {
    const numCategories = Object.keys(scores).length;
    return Object.fromEntries(
      Object.keys(scores).map(category => [category, 1 / numCategories])
    );
  }
}

function filterLowConfidenceIntents(scores: Scores, threshold = 0.2): Scores {
  return Object.fromEntries(Object.entries(scores).filter(([, score]) => score >= threshold));
}

export function classifySentence(
  sentence: string
): [Scores, number] {
  const startTime = Date.now();

  const regexScores = calculateRegexScores(sentence, keywords, regexWeight, highKeywords);
  const normalizedScores = normalizeScores(regexScores);
  const filteredScores = filterLowConfidenceIntents(normalizedScores, 0.2);

  const processingTime = (Date.now() - startTime) / 1000;
  return [filteredScores, processingTime];
}
